. ./env.sh

is_param() {
    local item match
    for item in "$@"; do
        if [ -z "$match" ]; then
            match="$item"
        elif [ "$match" = "$item" ]; then
            return 0
        fi
    done
    return 1
}

print_hdr() {
    h1="${blu}==>${rst} ${bld}"
    msg="$1"
    echo "\n${h1}$msg...${rst}"
}
